using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;/// <summary>/// 通用化管理器/// </summary>/// <typeparam name="T"></typeparam>public class UIM_UniversalManager<T> : Singleton<T>,IManager where T: UIM_UniversalManager<T>{    /// <summary>    /// 初始化管理器    /// </summary>    public virtual void InitManager()    {            }    //在场景开始时，将自身的刷新监听到场景加载中    public virtual void OnEnable()    {        SceneManager.sceneLoaded += (scene, mode) => InitManager();    }    public virtual void OnDisable()    {        SceneManager.sceneLoaded -= (scene, mode) => InitManager();    }}