using System.Collections;using System.Collections.Generic;using UnityEngine;using Sirenix.OdinInspector;using Unity.VisualScripting;using UIM;public class UIM_UnvisersalListPanel : MonoBehaviour{    //在这个类型中设置更多的list元素    [LabelText("生成位置")]    public GameObject scrollContent;    [LabelText("生成对象")]    public GameObject elementObj;    public virtual void OnEnable()    {              OnInitElements();    }    /// <summary>    /// 清空之前 已有对象    /// </summary>    public virtual void  OnCleanElements()    {        foreach (Transform child in scrollContent.transform)        {            Destroy(child.gameObject);        }    }    /// <summary>    /// 这里是生成方式    /// </summary>    public virtual void OnInitElements()    {        OnCleanElements();    }    /// <summary>    /// 添加一个子对象    /// </summary>    /// <returns></returns>    public virtual GameObject  OnAddElements()    {        GameObject element = Instantiate(elementObj, scrollContent.transform);        element.transform.SetParent(scrollContent.transform);        element.transform.localScale = Vector3.one;        //重建UI逻辑        UIM_UIManager.RebuildVerticalNavigation(scrollContent.transform);        return element;    }}