using System.Collections;using System.Collections.Generic;using UnityEngine;/*using UnityEngine.Localization;using UnityEngine.Localization.Settings;*/using Sirenix.OdinInspector;[CreateAssetMenu(fileName = "New OptionFuctionListSO", menuName = "Setting/OptionFuctionListSO")]public class OptionFuctionListSO : ScriptableObject{    [SerializeReference]    public List<OptionFuction> OptionFuctionList;    /// <summary>    /// 快速写名字    /// </summary>    //[Button]    public void QuickRename()    {        foreach (var item in OptionFuctionList)        {            var name = item.GetType().Name.Substring(3);            item.fuctionName = name;            item.fuctionNotion = $"Notion_{name}";        }           }   }