using UnityEngine;using UnityEngine.SocialPlatforms.Impl;using UnityEngine.UI;/// <summary>/// Add list of achievements to screen/// </summary>public class UIM_AchievenmentListPanel : UIM_UnvisersalListPanel{    public override void OnInitElements()    {        base.OnInitElements();        UIM_AchievenManager AM = UIM_AchievenManager.Instance;        if (!AM) return;        if (AM.AchievementList==null)        {            Debug.Log("成就数据为空");            return;        }        for (int i = 0; i < AM.AchievementList.Count; i ++)        {            var state = AM.AchievementList[i].State;            UIAchievement UIAchievement = OnAddElements().GetComponent<UIAchievement>();            UIAchievement.SetDisplay(AM.AchievementList[i]);                }    }}