// 更简单的工具类using Cysharp.Threading.Tasks;/// <summary>/// 全局工具，接口的入口/// </summary>public static class AudioFunclib{    private static AudioSystem _audioSystem;    // 注入入口（简化版，无需接口）    public static void SetAudioSystem(AudioSystem audioSystem)    {        _audioSystem = audioSystem;    }    // 快捷方法    public static void PlaySFX(string key) => _audioSystem?.PlaySFX(key);            //播放背景音乐        public static void PlayBGM(string key) => _audioSystem?.PlayBGM(key);        public static UniTask PlaySFXAsync(string key) =>        _audioSystem?.PlaySFXAsync(key) ?? UniTask.CompletedTask;}