using UnityEngine;using TMPro;using UnityEngine.UI;using UnityEngine.EventSystems;using UIM;public class UIM_SaveFileItem : MonoBehaviour{    public GameObject emptyObj;    [Space]    public GameObject createdObj;    public Button deleteBut;    [Space]    public GameObject _confimObj;    public Button _confimDeleteBut,_cancelDeleteBut;    public TMP_Text titleText,descText;    public TMP_Text percentageText;    /// <summary>    /// 根据输入so文件，来设置变现    /// </summary>    /// <param name="saveFileSO"></param>    public void SetDisplay(int i )    {        var saveFileSO = UIM_SaveFileManger.Instance.saveFileDatas[i];        //若存在        if (saveFileSO)        {            emptyObj.SetActive(false);            titleText.text = $"NO.{i}";            createdObj.SetActive(true);            _confimObj.SetActive(false);            //修改当前选中对象            EventSystem.current.SetSelectedGameObject(createdObj);        }        else        {            emptyObj.SetActive(true);            createdObj.SetActive(false);            _confimObj.SetActive(false);            //修改当前选中对象            EventSystem.current.SetSelectedGameObject(emptyObj);        }        UIM_UIManager.Instance.RefreshLayoutsRecursively();    }}