using System;using System.Collections;using System.Collections.Generic;using System.Text.RegularExpressions;using UnityEngine;public static class UIM_DebugManager {    public static Dictionary<string, string> statusPrefix = new Dictionary<string, string>    {        //类型        { "成功","<color=#149E45>[SUCCESS]</color>"},        { "错误","<color=#BE3030>[WARRING]</color>"},        { "注意","<color=#BC8000>[NOTION]</color>"},        //所属系统        { "UI","<color=#244FCF>[UI SYSTEM]</color>"},        { "数据","<color=#9740B0>[DATA SYSTEM]</color>"},        { "玩法","<color=#2087A1>[GAME PLAY]</color>"},        { "相机","<color=#b85113>[GAME PLAY]</color>"},            };    public static string Log(string message)    {        return Log(message,true);    }    public static string Log(string message, bool isSave = true)    {        return Log(message,"","", isSave);    }        // 打印调试信息    public static string Log(string message,string typeKey="",string systemKey="",bool isSave=true)    {        string typeP, systemP;        typeP = statusPrefix.ContainsKey(typeKey) ? statusPrefix[typeKey] : "";        systemP = statusPrefix.ContainsKey(systemKey) ? statusPrefix[systemKey] : "";        // 输出当前时间        DateTime currentTime = DateTime.Now;        string formattedTime = currentTime.ToString("yyyy-MM-dd HH:mm:ss");        message =  $"<color=#CDC727>{formattedTime}</color> {typeP} {systemP} {message}";        SendMessage(message, isSave);        return message;    }    public static void SendMessage(string message, bool isSave = true)    {        if (UIM_GoldFingerManager.Instance)        {            UIM_GoldFingerManager.Instance.OnAddLog(message);        }        // 存出到本地日志中        if(isSave)        LogToFile.SaveLogToFile(StripRichText(message));          }    // 去除富文本标签    public static string StripRichText(string input)    {        // 正则表达式匹配HTML标签        string pattern = "<.*?>"; // 匹配所有标签        string result = Regex.Replace(input, pattern, String.Empty); // 去除标签        return result;    }}