using UnityEngine;using TMPro;using UnityEngine.UI;using System;using System.Collections.Generic;using System.Linq;using UnityEngine.InputSystem;using UnityEngine.SceneManagement;using UIM;using UIM.Input;[AddComponentMenu("UIMaster/GoldFinger/GoldFingerManager")][DisallowMultipleComponent]public class UIM_GoldFingerManager : UIM_UniversalManager<UIM_GoldFingerManager>, IManager{    [Serializable]    public class  ButPanel    {        public Button but;        public GameObject panel;        public void InitBut()        {            if (but&&panel)            {                but.onClick.AddListener(delegate { UIM_UIManager.SwitchPanel(panel); });            }                   }                    }    [SerializeField]    public List<ButPanel> ButPanels;        public GameObject GFPanel;    public TMP_Text consoleText;        public void Start()    {                GFPanel.SetActive(false);        foreach (var butPanel in ButPanels)        {            butPanel.InitBut();        }        //给金手指加事件监听方法        UIM_InputSystem.GetPlayerInput().actions["CallGold"].performed += SwitchGoldPanel;                UIM_DebugManager.Log("\n程序已启动");    }    public override void InitManager()    {    }        void SwitchGoldPanel(InputAction.CallbackContext context)    {        if (UIM_SettingManager.GetValueByOption<SO_AllowedGF>()==0)        {            UIM_UIManager.SwitchPanel(GFPanel);        }    }    public  void OnAddLog(string message)    {        consoleText.text += message + "\n";    } }